var FieldVal = require('fieldval');
var BasicVal = require('fieldval-basicval');

var validator = new FieldVal({
	"my_key": 37
})

var my_key = validator.get("my_key", BasicVal.integer(true), BasicVal.minimum(40));

console.log(validator.end());

var my_number = validator.get(
	"my_number",
	BasicVal.float(
		{
			"required": true
			"parse": true,
			"stop_on_error": false
		}
	)
);

var validator = new FieldVal({
	"my_string" : "Testing"
})

var my_string = validator.get(
	"my_string",
	BasicVal.prefix(
		"ABC",
		{
			"stop_on_error" : false
		}
	),
	BasicVal.suffix(
		"XYZ"
	)
)

document.write("my_string: "+my_string);

document.write(
	"<pre>"+
		JSON.stringify(validator.end(),null,4)+
	"</pre>"
);

var my_check = function(value, emit){

	if(value!=="My Exact Value"){
		return {
			error: 1000,
			error_message: "I'm really picky. Enter 'My Exact Value'."
		}
	}

}

var minimum = function(min_val, flags) {

    var check = function(value) {
        if (value < min_val) {
            return {
	            error: 1000,
	            error_message: "Value was below " + min_val
	        }
        }
    }

    if(flags){
        //The check becomes part of the flag object, which is returned
        flags.check = check;
        return flags
    }

    return check;
}

var check = minimum(14);

var validator = new FieldVal({
    "my_money": "€7.54"
})

var my_money = validator.get("my_money", 
    BasicVal.string(true), 
    BasicVal.prefix("€"),
    function(value, emit){

        emit(value.substring(1))

    },
    BasicVal.float({parse:true}),
    BasicVal.minimum(5)
);

